mapper.select:
SELECT ADMIN 
FROM MEMBER
WHERE userid = #{adminid};

SELECT USERID , COMMENT, CCBAKDCD, CCBAASNO, CCBACTCD ,CCBAMNM1 ,COMMENTDATE 
FROM HERITAGEREVIEW
ORDER BY COMMENTDATE DESC;

mapper.delete:
DELETE R, H
FROM RATE R
JOIN HERITAGEREVIEW H ON H.USERID  = R.USERID
					  AND H.CCBAASNO = R.CCBAASNO
					  AND H.CCBAKDCD = R.CCBAKDCD
					  AND H.CCBACTCD = R.CCBACTCD
WHERE R.CCBAASNO = #{ccbaAsno}
  AND R.USERID = #{userid}
  AND R.CCBAKDCD = #{ccbaKdcd}
  AND R.CCBACTCD = #{ccbaCtcd};

mapper.update:
UPDATE MEMBER
SET ADMIN = #{admin}, ACCESS = #{access}
WHERE userid = #{userid};

mapper.select:
SELECT USERID , USERNAME ,REGDATE 
FROM MEMBER
ORDER BY USERID ASC;

mapper.update:
UPDATE HERITAGEREVIEW
SET userid = '탈퇴회원'
WHERE userid = #{userid};

UPDATE RATE
SET userid = '탈퇴회원'
WHERE userid = #{userid};
