mapper.select:
SELECT *
FROM MEMBER
WHERE userid = #{userid} AND userpw = #{userpw}

mapper.update:
		UPDATE MEMBER
  		SET IMAGEFILENAME = #{imagefilename}
 		WHERE USERID = #{userid}

mapper.select:
SELECT USERID, IMAGEFILENAME, IMAGEFILE 
FROM PROFILEIMAGE
WHERE userid = #{userid} AND IMAGEFILENAME = #{imagefilename};

mapper.delete:
DELETE
FROM BOOKMARK
WHERE USERID = #{userid} AND CCBAASNO = #{ccbaAsno};
