mapper.select:
SELECT *
FROM MEMBER
WHERE userid = #{userid} AND userpw = #{userpw}

SELECT USERID 
FROM MEMBER
WHERE USERNAME = #{username} AND USERPH = #{userph};

mapper.update:
UPDATE MEMBER
SET USERPW = #{userpw}
WHERE USERID = #{userid} AND USERPH = #{userph} AND EMAIL = #{email} AND USERNAME = #{username};

mapper.select:
