mapper.update:
UPDATE NOTICE
SET noticeUse = 'N'
WHERE noticeUse = 'Y';

UPDATE NOTICE
SET noticeDate = #{noticeDate},
    noticeTitle = #{noticeTitle},
    noticeContents = #{noticeContents},
    noticeUse = #{noticeUse}
WHERE noticeDate = #{noticeDate}
	AND noticeTitle = #{noticeTitle}
	AND noticeContents = #{noticeContents};
